package com.example.hw24

import android.os.Bundle
import androidx.fragment.app.Fragment
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.core.os.bundleOf
import androidx.navigation.fragment.findNavController
import com.example.hw24.databinding.FragmentFirstBinding

class FirstFragment : Fragment() {

    private lateinit var binding: FragmentFirstBinding
    private lateinit var adapter: CharactersAdapter
    private var charactersList = arrayListOf<Characters>()

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View {
        binding = FragmentFirstBinding.inflate(layoutInflater)
        return binding.root
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)
        adapter = CharactersAdapter(charactersList, this::onClick)

        charactersList.add(Characters("Harry Potter", "alive", "data:image/jpeg;base64,"))
        charactersList.add(Characters("Hermione Granger", "alive", "data:image/jpeg;base64,"))
        charactersList.add(Characters("Ron Weasley", "alive", "data:image/jpeg;base64,"))
        charactersList.add(Characters("Albus Dumbledore", "death", "data:image/jpeg;base64,"))
        charactersList.add(Characters("Lord Voldemort", "death", "data:image/jpeg;base64,"))

        binding.rvCharacters.adapter = adapter
    }

    private fun onClick(characters: Characters) {
        findNavController().navigate(R.id.secondFragment, bundleOf("harry" to characters))
    }
}